package vn.smartdev.invoice.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.invoice.dao.entity.Invoice;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.dao.repository.InvoiceDetailRepository;import vn.smartdev.invoice.dao.repository.InvoiceRepository;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class InvoiceDetailServiceImpl implements InvoiceDetailService {    @Autowired    InvoiceDetailRepository invoiceDetailRepository;    @Autowired    InvoiceRepository invoiceRepository;    @Override    public List<InvoiceDetail> getAll() {        return invoiceDetailRepository.findAll();    }    @Override    public InvoiceDetail findById(String id) {        return invoiceDetailRepository.findOne(id);    }    @Override    public void save(InvoiceDetail invoiceDetail) {        invoiceDetailRepository.save(invoiceDetail);    }    @Override    public void deleteInvoiceDetailsByInvoice(Invoice invoice) {        List<InvoiceDetail> invoiceDetails = invoiceDetailRepository.findByInvoice(invoice);        invoiceDetailRepository.deleteInBatch(invoiceDetails);    }    @Override    public void delete(InvoiceDetail invoiceDetail) {        invoiceDetailRepository.delete(invoiceDetail);    }    @Override    public List<InvoiceDetail> getInvoiceDetailByInvoiceId(String invoiceId) {        Invoice invoice = invoiceRepository.findOne(invoiceId);        return invoiceDetailRepository.findByInvoice(invoice);    }}