package vn.smartdev.invoice.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.invoice.dao.entity.Invoice;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.dao.repository.InvoiceDetailRepository;import vn.smartdev.invoice.dao.repository.InvoiceRepository;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class InvoiceServiceImpl implements InvoiceService {    @Autowired    InvoiceRepository invoiceRepository;    @Autowired    InvoiceDetailService invoiceDetailService;    @Override    public List<Invoice> getAll() {        return invoiceRepository.findAll();    }    @Override    public Invoice findById(String id) {        return invoiceRepository.findOne(id);    }    @Override    public void save(Invoice invoice) {        invoiceRepository.save(invoice);    }    @Override    public void deleteByinvoice(Invoice invoice) {        invoiceRepository.delete(invoice);    }    @Override    public void deleteById(String id) {        Invoice invoice = invoiceRepository.findOne(id);        invoiceDetailService.deleteInvoiceDetailsByInvoice(invoice);        invoiceRepository.delete(id);    }    @Override    public List<Invoice> findByUsername(String username) {        return invoiceRepository.findByUsername(username);    }}