package vn.smartdev.invoice.services;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.invoice.dao.entity.Invoice;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.dao.manager.InvoiceManager;import vn.smartdev.invoice.dao.repository.InvoiceRepository;import vn.smartdev.invoice.exception.InvoiceDetailNotFoundException;import vn.smartdev.invoice.dao.manager.InvoiceDetailManager;import vn.smartdev.invoice.exception.InvoiceNotFoundException;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class InvoiceDetailServicesImpl implements InvoiceDetailServices {    @Autowired    private InvoiceDetailManager invoiceDetailManager;    @Autowired    private InvoiceManager invoiceManager;    @Override    public List<InvoiceDetail> getAll() throws Exception {        return invoiceDetailManager.getAll();    }    @Override    public void save(InvoiceDetail invoiceDetail) throws Exception {        invoiceDetailManager.save(invoiceDetail);    }    @Override    public void delete(String invoiceDetailId) throws InvoiceDetailNotFoundException {        invoiceDetailManager.delete(invoiceDetailId);    }    @Override    public List<InvoiceDetail> getByInvoiceId(String invoiceId) throws InvoiceDetailNotFoundException, InvoiceNotFoundException {        Invoice invoice = invoiceManager.findById(invoiceId);        return invoiceDetailManager.findByInvoice(invoice);    }}