package vn.smartdev.invoicetest;import vn.smartdev.invoice.dao.entity.Invoice;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;/** * Created by Nguyen on 30/11/2016. */public class PrepareInvoice {    protected Map<String, Invoice> invoiceMap = new HashMap<String, Invoice>();    protected Map<String, Invoice> prepareinvoice(int number){        Map<String, Invoice> prepareinvoice = new HashMap<String, Invoice>();        for (int i = 0; i < number; i ++){            Invoice invoice = new Invoice("invoice of " + i );            prepareinvoice.put("invoiceMap " + i, invoice);        }        return prepareinvoice;    }    protected List<Invoice> getListinvoice(Map<String, Invoice> invoiceMap){        List<Invoice> invoices = new ArrayList<Invoice>();        for (int i = 0; i < invoiceMap.size(); i ++){            invoices.add(invoiceMap.get("invoiceMap " + i));        }        return invoices;    }}