package vn.smartdev.invoicetest;import vn.smartdev.invoice.dao.entity.Invoice;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.dao.entity.Status;import vn.smartdev.invoice.dao.model.CartModel;import vn.smartdev.invoice.dao.model.InvoiceModel;import vn.smartdev.product.dao.entity.ProductDetail;import java.sql.Date;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;/** * Created by Nguyen on 30/11/2016. */public class PrepareInvoice {    protected Map<String, Invoice> invoiceMap = new HashMap<String, Invoice>();    protected Map<String, Invoice> prepareinvoice(int number){        Map<String, Invoice> prepareinvoice = new HashMap<String, Invoice>();        for (int i = 0; i < number; i ++){            Invoice invoice = new Invoice("invoice of " + i );            prepareinvoice.put("invoiceMap " + i, invoice);        }        return prepareinvoice;    }    protected List<Invoice> getListinvoice(Map<String, Invoice> invoiceMap){        List<Invoice> invoices = new ArrayList<Invoice>();        for (int i = 0; i < invoiceMap.size(); i ++){            invoices.add(invoiceMap.get("invoiceMap " + i));        }        return invoices;    }    protected InvoiceModel prepareInvoice(){        InvoiceModel invoice = new InvoiceModel();        invoice.setAddress("NPN");        invoice.setStatus(Status.NotYet);        invoice.setEmail("toannxm.it");        invoice.setCity("DN");        invoice.setFirstName("Toan");        invoice.setLastName("Minh");        invoice.setPhone("0936054481");        invoice.setUsername("toan");        invoice.setInvoiceDate(Date.valueOf("2016-10-12"));        InvoiceDetail invoiceDetail = new InvoiceDetail();        invoiceDetail.setDiscount(10);        invoiceDetail.setProductDetail(prepareProduct());        invoiceDetail.setQuantity(1);        invoiceDetail.setAmount(100);        List<InvoiceDetail> invoiceDetails = new ArrayList<InvoiceDetail>();        invoiceDetails.add(invoiceDetail);        invoice.setInvoiceDetails(invoiceDetails);        return invoice;    }    protected List<CartModel> prepareCart(){        List<CartModel> cartModels = new ArrayList<CartModel>();        CartModel cartModel = new CartModel();        cartModel.setProductDetail(prepareProduct());        cartModel.setDiscount(10);        cartModel.setQuantity(1);        return cartModels;    }    protected ProductDetail prepareProduct(){        ProductDetail productDetail = new ProductDetail("abc", 100, 10, 1, "DN");        return productDetail;    }}