package vn.smartdev.order.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.order.dao.entity.OrderDetail;import vn.smartdev.order.dao.repository.OrderDetailRepository;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class OrderDetailServiceImpl implements OrderDetailService {    @Autowired    OrderDetailRepository orderDetailRepository;    @Override    public List<OrderDetail> getAll() {        return orderDetailRepository.findAll();    }    @Override    public OrderDetail findById(String id) {        return orderDetailRepository.findOne(id);    }    @Override    public void save(OrderDetail orderDetail) {        orderDetailRepository.save(orderDetail);    }    @Override    public void delete(OrderDetail orderDetail) {        orderDetailRepository.delete(orderDetail);    }}