package vn.smartdev.order.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.order.dao.entity.OrderHistory;import vn.smartdev.order.dao.repository.OrderHistoryRepository;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class OrderHistoryServiceImpl implements OrderHistoryService{    @Autowired    OrderHistoryRepository orderHistoryRepository;    @Override    public List<OrderHistory> getAll() {        return orderHistoryRepository.findAll();    }    @Override    public OrderHistory findById(String id) {        return orderHistoryRepository.findOne(id);    }    @Override    public void save(OrderHistory orderHistory) {        orderHistoryRepository.save(orderHistory);    }    @Override    public void delete(OrderHistory orderHistory) {        orderHistoryRepository.delete(orderHistory);    }    @Override    public List<OrderHistory> findByOrderId(String orderId) {        return null;    }}