package vn.smartdev.order.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.order.dao.entity.Order;import vn.smartdev.order.dao.repository.OrderRepository;import java.util.List;/** * Created by Nguyen on 30/11/2016. */@Servicepublic class OrderServiceImpl implements OrderService {    @Autowired    OrderRepository orderRepository;    @Override    public List<Order> getAll() {        return orderRepository.findAll();    }    @Override    public Order findById(String id) {        return orderRepository.findOne(id);    }    @Override    public void save(Order order) {        orderRepository.save(order);    }    @Override    public void deleteByOrder(Order order) {        orderRepository.delete(order);    }    @Override    public void deleteById(String id) {        orderRepository.delete(id);    }    @Override    public List<Order> findByUsername(String username) {        return orderRepository.findByUsername(username);    }}