package vn.smartdev.ordertest;import vn.smartdev.order.dao.entity.Order;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;/** * Created by Nguyen on 30/11/2016. */public class PrepareOrder {    protected Map<String, Order> orderMap = new HashMap<String, Order>();    protected Map<String, Order> prepareOrder(int number){        Map<String, Order> prepareOrder = new HashMap<String, Order>();        for (int i = 0; i < number; i ++){            Order order = new Order("order of " + i );            prepareOrder.put("orderMap " + i, order);        }        return prepareOrder;    }    protected List<Order> getListOrder(Map<String, Order> orderMap){        List<Order> orders = new ArrayList<Order>();        for (int i = 0; i < orderMap.size(); i ++){            orders.add(orderMap.get("orderMap " + i));        }        return orders;    }}