package vn.smartdev.product.servicesImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.product.dao.entity.Discount;import vn.smartdev.product.dao.entity.Product;import vn.smartdev.product.dao.model.DiscountModel;import vn.smartdev.product.dao.repository.DiscountRepository;import vn.smartdev.product.services.DiscountServices;import vn.smartdev.product.services.ProductServices;import java.util.List;/** * Created by Nguyen on 16/12/2016. */@Servicepublic class DiscountServicesImpl implements DiscountServices {    @Autowired    DiscountRepository discountRepository;    @Autowired    ProductServices productServices;    @Override    public Discount findByProduct(Product product) {        return discountRepository.findByProduct(product);    }    @Override    public List<Discount> listAll() {        return discountRepository.findAll();    }    @Override    public void deleteDiscount(String discountID) {        discountRepository.delete(discountID);    }    @Override    public void save(DiscountModel discountModel) {        Discount discount = new Discount();        Product product = productServices.getProduct(discountModel.getProductId());        discount.setProduct(product);        discount.setStartDate(discountModel.getStartDate());        discount.setEndDate(discountModel.getEndDate());        discount.setDiscount(discountModel.getDiscount());        discountRepository.save(discount);    }}