package vn.smartdev.role.dao.entity;import vn.smartdev.core.jpa.auditing.AbstractAuditableEntity;import javax.persistence.*;import java.util.UUID;/** * The persistent class for the role database table. * */@Entity@Table (name = "role")public class Role extends AbstractAuditableEntity<String> {    private static final long serialVersionUID = 1L;    @Id    private String id = UUID.randomUUID().toString();    private String name;    public Role() {    }    public Role(String id) {        super();        this.id = id;    }    public String getId() {        return this.id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return this.name;    }    public void setName(String name) {        this.name = name;    }}