package vn.smartdev.role.manager;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import vn.smartdev.role.dao.entity.Role;import vn.smartdev.role.dao.repository.RoleRepository;import java.util.List;/** * Created by Nguyen on 27/11/2016. */@Componentpublic class RoleManagerImpl implements RoleManager {    @Autowired    RoleRepository roleRepository;    @Override    public List<Role> getAll() {        return roleRepository.findAll();    }}