package vn.smartdev.user.services;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.smartdev.user.dao.entity.Role;import vn.smartdev.user.dao.repository.RoleRepository;import vn.smartdev.user.dao.manager.RoleManager;import java.util.List;/** * Created by Nguyen on 27/11/2016. */@Servicepublic class RoleServiceImpl implements RoleManager {    @Autowired    RoleRepository roleRepository;    @Override    public List<Role> getAll() {        return roleRepository.findAll();    }    @Override    public Role findRoleByName(String rolename) throws javax.management.relation.RoleNotFoundException {        return roleRepository.findByRoleName(rolename);    }}