package vn.smartdev;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.transaction.annotation.Transactional;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import vn.smartdev.order.dao.entity.Order;import vn.smartdev.order.dao.repository.OrderRepository;import vn.smartdev.order.manager.OrderService;import javax.servlet.http.HttpServletRequest;import java.text.DateFormat;import java.util.Date;import java.util.List;import java.util.Locale;/** * Created by Nguyen on 30/11/2016. */@Controller@RequestMapping(value = "/admin")public class OrderController {    private static final Logger logger = LoggerFactory.getLogger(OrderController.class);    @Autowired    OrderService orderService;    @Transactional    @RequestMapping(value = {"/order","/order/*"}, method = RequestMethod.GET)    public String order(Locale locale, Model model) {        logger.info("Welcome order! The client locale is {}.", locale);        Date date = new Date();        DateFormat dateFormat = DateFormat.getDateTimeInstance(DateFormat.LONG, DateFormat.LONG, locale);        String formattedDate = dateFormat.format(date);        model.addAttribute("serverTime", formattedDate );        List<Order> orders = orderService.getAll();        model.addAttribute("orders",orders);        return "orderPage";    }    @Transactional    @RequestMapping(value = "/order/update", method = RequestMethod.GET)    public String updateOrder(Locale locale, Model model, HttpServletRequest request) {        logger.info("Welcome update order ! The client locale is {}.", locale);        Date date = new Date();        DateFormat dateFormat = DateFormat.getDateTimeInstance(DateFormat.LONG, DateFormat.LONG, locale);        String formattedDate = dateFormat.format(date);        model.addAttribute("serverTime", formattedDate );        String status = request.getParameter("status");        return "redirect:order";    }}