package vn.smartdev.app;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import vn.smartdev.category.dao.entity.Category;import vn.smartdev.category.dao.repository.CategoryRepository;/** * Created by Nguyen on 01/12/2016. */public class App{    public static void main( String[] args ) throws InterruptedException    {        @SuppressWarnings("resource")        ApplicationContext context =                new ClassPathXmlApplicationContext("spring-data.xml");        CategoryRepository categoryRepository = (CategoryRepository) context.getBean("categoryRepository");        Category category = new Category();        category.setCategoryName("Category new");        categoryRepository.save(category);        System.out.println(categoryRepository.findAll());    }}