package vn.smartdev.controller;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.transaction.annotation.Transactional;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.exception.InvoiceDetailNotFoundException;import vn.smartdev.invoice.exception.InvoiceNotFoundException;import vn.smartdev.invoice.services.InvoiceDetailServices;import java.util.List;import java.util.Locale;/** * Created by Nguyen on 02/12/2016. */@Controller@RequestMapping(value = "/admin")public class InvoiceDetailController {    private static final Logger logger = LoggerFactory.getLogger(InvoiceDetailController.class);    @Autowired    InvoiceDetailServices invoiceDetailServices;    @Transactional    @RequestMapping(value = {"/invoiceDetail"}, method = RequestMethod.GET)    public String invoice(@RequestParam("id") String invoiceId, Model model) throws InvoiceDetailNotFoundException, InvoiceNotFoundException {        List<InvoiceDetail> invoiceDetails = invoiceDetailServices.getByInvoiceId(invoiceId);        model.addAttribute("invoiceDetails", invoiceDetails);        return "adminInvoiceDetailPage";    }}