package vn.smartdev.controller;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.transaction.annotation.Transactional;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import vn.smartdev.invoice.dao.entity.InvoiceDetail;import vn.smartdev.invoice.manager.InvoiceDetailService;import java.text.DateFormat;import java.util.Date;import java.util.List;import java.util.Locale;/** * Created by Nguyen on 02/12/2016. */@Controller@RequestMapping(value = "/admin")public class InvoiceDetailController {    private static final Logger logger = LoggerFactory.getLogger(InvoiceDetailController.class);    @Autowired    InvoiceDetailService invoiceDetailService;    @Transactional    @RequestMapping(value = {"/invoiceDetail"}, method = RequestMethod.GET)    public String invoice(@RequestParam("id") String invoiceId, Locale locale, Model model) {        logger.info("Welcome invoice! The client locale is {}.", locale);        Date date = new Date();        DateFormat dateFormat = DateFormat.getDateTimeInstance(DateFormat.LONG, DateFormat.LONG, locale);        String formattedDate = dateFormat.format(date);        model.addAttribute("serverTime", formattedDate );        List<InvoiceDetail> invoiceDetails = invoiceDetailService.getInvoiceDetailByInvoiceId(invoiceId);        model.addAttribute("invoiceDetails", invoiceDetails);        return "adminInvoiceDetailPage";    }}