package vn.smartdev.controller.controller;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.transaction.annotation.Transactional;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import vn.smartdev.invoice.dao.entity.Invoice;import vn.smartdev.invoice.manager.InvoiceService;import javax.servlet.http.HttpServletRequest;import java.util.List;import java.util.Locale;/** * Created by Nguyen on 30/11/2016. */@Controller@RequestMapping(value = "/admin")public class InvoiceController {    private static final Logger logger = LoggerFactory.getLogger(InvoiceController.class);    @Autowired    InvoiceService invoiceService;    @Transactional    @RequestMapping(value = {"/invoice"}, method = RequestMethod.GET)    public String invoice(Locale locale, Model model) {        List<Invoice> invoices = invoiceService.getAll();        for (Invoice invoice : invoices) {            System.out.println(invoice.getUsername());            System.out.println(invoice.getStatus().toString());        }        model.addAttribute("invoices", invoices);        return "adminInvoicePage";    }    @Transactional    @RequestMapping(value = {"/deleteInvoice"}, method = RequestMethod.GET)    public String detleteInvoice(@RequestParam("id") String id, Locale locale, Model model) {        invoiceService.deleteById(id);        return "redirect:invoice";    }    @Transactional    @RequestMapping(value = "/updateInvoice", method = RequestMethod.GET)    public String updateInvoice(@RequestParam("id") String id, Locale locale, Model model, HttpServletRequest request) {        String status = request.getParameter("status");        return "redirect:invoice";    }}